                        }
                    }
                }
            }
        }

        // Reset all zones for next round
        _resetAllZones();

        // Start new round
        roundNumber++;

        emit Distribution(_winningZone, totalFunds, roundNumber);
        emit RoundStarted(roundNumber);
    }

