        // Reset all zones for next round
        _resetAllZones();

        // Start new round
        roundNumber++;

        emit RoundStarted(roundNumber);
    }

    function _resetAllZones() private {
        for (uint256 i = 0; i < 5; i++) {
            Zone zone = Zone(i);
            ZoneInfo storage zoneInfo = zones[zone];

            for (uint256 j = 0; j < zoneInfo.depositorCount; j++) {
